export function getShopItems(shopId: string): MenuItem[] {
  return MENU_ITEMS.filter(item => item.shop === shopId);
}

export function getHealthyPercentage(shopId: string): number {
  const items = getShopItems(shopId);
  const healthyCount = items.filter(item => item.isHealthy).length;
  return Math.round((healthyCount / items.length) * 100);
}